import { useState, useEffect } from "react";

const useCounter = () => {
    const [counter, setCounter] = useState(0);

    useEffect((forwards = true) => {

        const interval = setInterval(() => {
            if (forwards === true) {
                setCounter((prev) => {
                    return prev + 1
                })
            }

        }, 1000);

        return () => clearInterval(interval);
    }, []);
    return counter
}

export default useCounter;
